if(data.premiumFinanceSelect == 'yes'){
  var month =moment(data.start_date).month() +1;
  var day = moment(data.start_date).format('DD');
  if((day == 30 && month == 6) || month == 7){
    value = (parseFloat(data.totalAmount).toFixed(2))*25/100;
    data.paymentInstallments = 6;
  } else if(month == 8){
    value = (parseFloat(data.totalAmount).toFixed(2))*30/100;
    data.paymentInstallments = 5;
  }else if(month == 9){
    value = (parseFloat(data.totalAmount).toFixed(2))*35/100;
    data.paymentInstallments = 4;
  } else if(month == 10){
    value = (parseFloat(data.totalAmount).toFixed(2))*40/100;
    data.paymentInstallments = 3;
  } else {
    value = parseFloat(data.totalAmount).toFixed(2);
    data.paymentInstallments = 0;
  }
} else {
value = parseFloat(data.totalAmount).toFixed(2);
data.paymentInstallments = 0;
}

