    value = (parseFloat(data.totalAmount).toFixed(2))*35/100;
    data.paymentInstallments = 5;
  }else if(month == 9){
    value = (parseFloat(data.totalAmount).toFixed(2))*40/100;
    data.paymentInstallments = 4;
  } else if(month == 10){
    value = (parseFloat(data.totalAmount).toFixed(2))*45/100;
    data.paymentInstallments = 3;
  } else {
    value = parseFloat(data.totalAmount).toFixed(2);
    data.paymentInstallments = 0;
  }
} else {
value = parseFloat(data.totalAmount).toFixed(2);
