data.functions = {
  userApprovedChanged: () => {
    if(data.userApproved == 'rejected' && (data.proposalCountFlag == false || data.proposalCountFlag == "false")){
      data.proposalCount = (data.proposalCount == "") ? 0 : data.proposalCount;
      data.proposalCount = parseInt(data.proposalCount) + 1;
      data.proposalCountFlag = true;
    }
  },
  paymentVerifiedChanged: () => {
    if(data.paymentVerified == 'declined' && (data.proposalCountFlag == false || data.proposalCountFlag == "false")){
      data.proposalCount = (data.proposalCount == "") ? 0 : data.proposalCount;
      data.proposalCount = parseInt(data.proposalCount) + 1;
      data.userApproved = "rejected";
      data.proposalCountFlag = true;
    }else if(data.paymentVerified == "approved"  && (data.proposalCountFlag == false || data.proposalCountFlag == "false")){
      data.proposalCount = (data.proposalCount == "") ? 0 : data.proposalCount;
      data.proposalCount = parseInt(data.proposalCount) + 1;
      data.proposalCountFlag = true;
    }
  }
};
